class BSTIterator {
public:
    vector<int>keys;
    int idx;
    void traverse(TreeNode *root, vector<int>&keys_v)
    {
        if (root==NULL) return;
        traverse(root->left, keys_v);
        keys_v.push_back(root->val);
        traverse(root->right, keys_v);
    }
    BSTIterator(TreeNode* root) {
        traverse(root, keys);
        idx=0;
    }

    int next() {
        return keys[idx++];
    }

    bool hasNext() {
        return idx < keys.size();
    }
};
